function CustomerBenefits() {
    const benefits = [
      {
        icon: <Discount />,
        title: "Exclusive Discounts",
        description: "Save up to 30% on your dream home.",
      },
      {
        icon: <Verified />,
        title: "Verified Listings",
        description: "Confidence in every property with thorough checks.",
      },
      {
        icon: <Booking />,
        title: "Seamless Booking",
        description: "Book your property easily through our platform.",
      },
      {
        icon: <Legal />,
        title: "Legal Compliance",
        description: "All properties come with verified documents and approvals.",
      },
      {
        icon: <Assistance />,
        title: "Personalized Assistance",
        description: "Dedicated support for every step of your journey.",
      },
    ];
  
    const containerVariants = {
      hidden: { opacity: 0 },
      visible: {
        opacity: 1,
        transition: {
          staggerChildren: 0.2,
        },
      },
    };
  
    const itemVariants = {
      hidden: { opacity: 0, y: 20 },
      visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
    };
  
    // Use the IntersectionObserver to track visibility
    const { ref, inView } = useInView({
      triggerOnce: false, // Ensures animation triggers only once
      threshold: 0.3,    // Triggers when 10% of the component is visible
    });
  
    return (
      <motion.div
        ref={ref} // Attach ref for intersection observer
        className="bg-[#F9F6FF] p-6"
        initial="hidden"
        animate={inView ? "visible" : "hidden"}
        variants={containerVariants}
      >
        <h1 className="font-meuthanies md:mt-8 text-4xl md:mb-6 text-center">
          Customer Benefits
        </h1>
        <div className="flex justify-center">
          <div>
            {benefits.map((benefit, index) => (
              <motion.div
                key={index}
                className="space-y-6"
                variants={itemVariants}
              >
                <hr className="mt-8 md:w-[550px] mx-auto" />
                <div className="flex items-start gap-4">
                  <div className="text-primary">{benefit.icon}</div>
                  <div className="md:ml-16 ml-4 font-sf-pro">
                    <h2 className="font-semibold text-xl">{benefit.title}</h2>
                    <p className="text-gray-600">{benefit.description}</p>
                  </div>
                </div>
              </motion.div>
            ))}
            <hr className="mt-8 md:w-[550px] md:mb-16 mx-auto" />
          </div>
        </div>
      </motion.div>
    );
  }
  
  export default CustomerBenefits;